import styles from "./styles/icons.module.css";

export const Arrow = ({ size, color, direction }) => {
  const fill = color ? color : "currentColor";
  const iconstyle = { width: size, height: size };
  let rotation = 0;

  switch (direction) {
    case "left":
      rotation = 180;
      break;
    case "up":
      rotation = -90;
      break;
    case "down":
      rotation = 90;
      break;
    case "right":
      rotation = 0;
      break;
    default:
      rotation = 0;
      break;
  }

  return (
    <div className={styles.isIcon} style={iconstyle}>
      <svg width="100%" height="100%" viewBox="0 0 26 25" fill="none" xmlns="http://www.w3.org/2000/svg" style={{ transform: `rotate(${rotation}deg)` }}>
        <path
          d="M12.3366 18.3996C11.9705 18.7657 11.9705 19.3593 12.3366 19.7254C12.7027 20.0915 13.2963 20.0915 13.6624 19.7254L20.2249 13.1629C20.4069 12.981 20.4984 12.7428 20.4995 12.5043L20.4995 12.5L20.4995 12.4986C20.4993 12.3703 20.4734 12.2481 20.4266 12.1368C20.3847 12.0372 20.325 11.9433 20.2472 11.8601C20.2391 11.8514 20.2308 11.8429 20.2223 11.8345L13.6624 5.27459C13.2963 4.90847 12.7027 4.90847 12.3366 5.27459C11.9705 5.6407 11.9705 6.2343 12.3366 6.60041L17.2987 11.5625H6.43701C5.91925 11.5625 5.49951 11.9822 5.49951 12.5C5.49951 13.0178 5.91925 13.4375 6.43701 13.4375H17.2987L12.3366 18.3996Z"
          fill={fill}
        />
      </svg>
    </div>
  );
};

